class Lista extends React.Component {
    constructor(props) {
        super(props)
        this.state = { itens: [], produto: '', preco: 0};
        this.setProduto = this.setProduto.bind(this);
        this.adicionarProduto = this.adicionarProduto.bind(this);
    }

    render() {
        return (
            <div className="lista">
                <div className="divHeaderLista">{"Título da lista"}
                    <br/>
                    Adicionar item <input type="text" className="campoHeaderItem" onChange={this.setProduto} value={this.state.produto}></input> Qtd. <input type="number" min="1" className="campoHeaderQtd" onChange={this.setPreco} value={this.state.preco}></input> <button className="btnAdicionar" onClick={this.adicionarProduto}>Adicionar</button>
                </div>
                <div className="conteudo">
                    <Item itens={this.state.itens}/>
                </div>
                <div>Total R$ {this.state.total}</div>
            

          {/*<div>
            <h3>Tarefas</h3>
            <TodoList items={this.state.items} />
            <form onSubmit={this.handleSubmit}>
              <label htmlFor="new-todo">
                O que precisa ser feito?
              </label>
              <input
                id="new-todo"
                onChange={this.handleChange}
                value={this.state.text}
              />
              <button>
                Adicionar #{this.state.items.length + 1}
              </button>
            </form>
          </div>*/}

            </div>
        );//fim do return
      }//fim do render

      setProduto(e) {
        this.setState({ produto: e.target.value });
      }

      setPreco(e){
        this.setState({ preco: e.target.value });
      }

      adicionarProduto(e) {
        e.preventDefault();
        if (this.state.produto.length === 0 && this.state.preco === 0) {
          return;
        }
        const newItem = {
          produto: this.state.produto,
          id: Date.now(),
          preco: this.state.preco
        };
        this.setState(state => ({
          itens: state.itens.concat(newItem),
          produto: '',
          preco: 0
        }));
      }

}//fim da classe (component) lista

function Item (props) {
    return <> {props.itens.map(item => (
            <div className="umItem" key={item.id}>
                <input type="checkbox" name="riscar" id=""></input>
                <input type="text" value={item.produto} className="campo" id="tfProd"></input>
                <label for="preco"> R$ </label>
                <input id="preco" value={item.preco} type="text" className="campo" id="tfPreco"></input>
                <label for="qtd">   Qtd. </label>
                <input id="qtd" type="number" min="1" className="campo" id="tfQtd"></input>
                <span> Subtotal R$ </span>
           </div>
           ))}
          </>
}

ReactDOM.render(
    <Lista></Lista>,
    document.getElementById('root')
)
